//note: x and y are map coordinates, not pixel coordinates! divide by 32 (>>5) first
bool CollisionDetection::validMove(int x, int y){
	if (location == SURFACE)
		return map[y][x].isPassable();
	else //location == UNDERGROUND
		return undergroundMap[y][x].isPassable();
}

//only draws what the camera sees
void CollisionDetection::drawMap(){
	if (location == SURFACE)
	{
		for (int row = camera->rangeY1(); row < camera->rangeY2(); row++){
			for (int col = camera->rangeX1(); col < camera->rangeX2(); col++){
				window->draw(*(map[row][col].getSprite()));
				if (!animationMap[row][col].ignorable())
					window->draw(*(animationMap[row][col].getSprite()));
			}
		}
	}
	else if (location == UNDERGROUND)
	{
		for (int row = camera->rangeY1(); row < camera->rangeY2(); row++){
			for (int col = camera->rangeX1(); col < camera->rangeX2(); col++){
				window->draw(*(undergroundMap[row][col].getSprite()));
				if (!undergroundAnimationMap[row][col].ignorable())
					window->draw(*(undergroundAnimationMap[row][col].getSprite()));
			}
		}
	}
}

void CollisionDetection::spawnEnemies(std::vector<Enemy>& e){
	if (location == SURFACE)
	{
		for (int row = camera->rangeY1(); row < camera->rangeY2(); row++){
			for (int col = camera->rangeX1(); col < camera->rangeX2(); col++){
				if (spawnMap[row][col] == 1)
				{
					e.push_back(Enemy(20, 1, 1, 1, (col << 5), (row << 5), 32));
					spawnMap[row][col] = 0;
				}
			}
		}
	}
	else if (location == UNDERGROUND)
	{
		for (int row = camera->rangeY1(); row < camera->rangeY2(); row++){
			for (int col = camera->rangeX1(); col < camera->rangeX2(); col++){
				if (undergroundSpawnMap[row][col] == 1)
				{
					e.push_back(Enemy(20, 1, 1, 1, (col << 5), (row << 5), 32));
					undergroundSpawnMap[row][col] = 0;
				}
			}
		}
	}
}

void CollisionDetection::changeLocation(location_t loc){
	location = loc;
}

CollisionDetection::location_t CollisionDetection::getLocation(){
	return location;
}